function getCurrentArticleUrl() {
    const path = window.location.pathname;
    const cleanPath = path.replace(/^\/+|\/+$/g, '');
    return cleanPath;
}

function getCurrentArticle() {
    const currentUrl = getCurrentArticleUrl();
    return window.allArticles.find(article => article.article === currentUrl);
}

function renderSameArtArticles() {
    const container = document.querySelector('.sameart-articles');
    if (!container) return;
    
    const currentArticle = getCurrentArticle();
    
    if (!currentArticle) {
        container.innerHTML = '<p>Статьи по похожей теме не найдены</p>';
        return;
    }

    const currentCategories = (currentArticle.categories || [])
        .filter(category => category !== 'Новая');
    
    if (currentCategories.length === 0) {
        container.innerHTML = '<p>Статьи по похожей теме не найдены</p>';
        return;
    }
    
    const filteredArticles = window.allArticles
        .filter(article => {
            if (article.article === currentArticle.article) return false;
            
            if (!article.categories || article.categories.length === 0) return false;
            
            return article.categories.some(category => {

                if (category === 'Новая') return false;
                return currentCategories.includes(category);
            });
        })
        .sort((a, b) => new Date(b.date) - new Date(a.date))
        .slice(0, 3);
    
    if (filteredArticles.length === 0) {
        container.innerHTML = '<p>Статьи по похожей теме не найдены</p>';
        return;
    }
    
    const cardsHTML = filteredArticles.map(article => createSameArtCard(article)).join('');
    container.innerHTML = cardsHTML;
}

function initSameArtArticles() {
    if (window.allArticles && window.allArticles.length > 0) {
        renderSameArtArticles();
    } else {
        const checkArticles = setInterval(() => {
            if (window.allArticles && window.allArticles.length > 0) {
                clearInterval(checkArticles);
                renderSameArtArticles();
            }
        }, 100);
    }
}

