document.addEventListener('click', (e) => {
    const shareButton = e.target.closest('.share-button');
    const dropdown = document.querySelector('.share-dropdown');
    
    if (dropdown && !e.target.closest('.share-button') && !e.target.closest('.share-dropdown')) {
        closeDropdown();
    }
    
    if (shareButton) {
        if (dropdown && dropdown.parentElement.contains(shareButton)) {
            closeDropdown();
            return;
        }
        
        createDropdown(shareButton);
    }
});

function createDropdown(button) {
    const url = window.location.href;
    const encodedUrl = encodeURIComponent(url);
    const title = encodeURIComponent(document.title);
    const text = encodeURIComponent('Вот это статья!');
    
    const networks = {
        vk: {
            url: `https://vk.com/share.php?url=${encodedUrl}&title=${title}`,
            name: 'Вконтакте',
            icon: 'vk-icon'
        },
        telegram: {
            url: `https://t.me/share/url?url=${encodedUrl}&text=${text}`,
            name: 'Телеграм',
            icon: 'telegram-icon'
        },
        ok: {
            url: `https://connect.ok.ru/dk?st.cmd=WidgetSharePreview&st.shareUrl=${encodedUrl}`,
            name: 'Одноклассники',
            icon: 'ok-icon'
        },
        copy: {
            url: '#',
            name: 'Скопировать',
            icon: 'copy-icon'
        }
    };
    
    const dropdown = document.createElement('div');
    dropdown.className = 'share-dropdown';
    
    let html = '';
    for (let key in networks) {
        const network = networks[key];
        html += `
            <a href="${network.url}" class="share-item ${key === 'copy' ? 'copy-link' : ''}" 
               target="${key === 'copy' ? '_self' : '_blank'}" rel="${key === 'copy' ? '' : 'noopener noreferrer'}">
                <div class="icon ${network.icon}"></div>
                <div class="text">${network.name}</div>
            </a>
        `;
    }
    
    dropdown.innerHTML = html;
    button.style.position = 'relative';
    button.appendChild(dropdown);
    
    positionDropdownAboveButton(dropdown, button);
    
    const copyButton = dropdown.querySelector('.copy-link');
    if (copyButton) {
        copyButton.addEventListener('click', function(e) {
            e.preventDefault();
            e.stopPropagation();
            copyLink(e, url);
        });
    }
    
    setTimeout(() => dropdown.classList.add('active'), 10);
}

function positionDropdownAboveButton(dropdown, button) {
    const buttonRect = button.getBoundingClientRect();
    const dropdownHeight = dropdown.offsetHeight;

    dropdown.style.top = 'auto';
    dropdown.style.bottom = '100%';
    dropdown.style.right = '0';
    dropdown.style.left = 'auto';
    dropdown.style.marginTop = '0';
    dropdown.style.marginBottom = '8px';
}

function closeDropdown() {
    const dropdown = document.querySelector('.share-dropdown');
    if (dropdown) {
        dropdown.classList.remove('active');
        setTimeout(() => {
            if (dropdown.parentElement) {
                dropdown.parentElement.style.position = '';
            }
            dropdown.remove();
        }, 200);
    }
}

function copyLink(e, url) {
    e.preventDefault();
    e.stopPropagation();
    
    navigator.clipboard.writeText(url).then(() => {
        const button = e.currentTarget;
        const originalText = button.querySelector('.text').textContent;
        button.querySelector('.text').textContent = 'Скопировано!';
        button.style.background = '#e8f5e8';
        button.style.color = '#28a745';
        
        setTimeout(() => {
            button.querySelector('.text').textContent = originalText;
            button.style.background = '';
            button.style.color = '';
            closeDropdown();
        }, 1500);
    }).catch(() => {
        fallbackCopy(url);
    });
}

function fallbackCopy(text) {
    const textArea = document.createElement('textarea');
    textArea.value = text;
    textArea.style.position = 'fixed';
    textArea.style.opacity = '0';
    document.body.appendChild(textArea);
    textArea.focus();
    textArea.select();
    document.execCommand('copy');
    document.body.removeChild(textArea);
    closeDropdown();
}


//Прокрутка до ссылки в статье до определенного расстояния


document.addEventListener('DOMContentLoaded', function() {
    setTimeout(function() {
        document.body.addEventListener('click', function(e) {
            const link = e.target.closest('a[href^="#"]');

            if (link && link.closest('.article-content') && link.getAttribute('href') !== '#') {
                e.preventDefault();
                e.stopImmediatePropagation();
                
                const targetId = link.getAttribute('href');
                const targetElement = document.querySelector(targetId);
                
                if (targetElement) {
                    const targetPosition = targetElement.getBoundingClientRect().top + window.pageYOffset - 100;
                    
                    window.scrollTo({
                        top: targetPosition,
                        behavior: 'smooth'
                    });
                    
                    if (history.pushState) {
                        history.pushState(null, null, targetId);
                    } else {
                        location.hash = targetId;
                    }
                }
            }
        }, true);
    }, 1000);
});


