//Автоматический типограф


function applyNbsp() {
    const elements = document.querySelectorAll('h1, h2, h3, h4, h5, h6, p, li, td, th, span, div');
    
    elements.forEach(el => {
        if (el.children.length > 0 && !isSimpleTextElement(el)) {
            return;
        }
        
        let html = el.innerHTML;
        if (!html.trim()) return; 

        html = html.replace(/(\d+)\s/g, '$1&nbsp;');
        html = html.replace(/(\s)(-|\u2013)(\s)/g, '$1&#8209;$3');
        html = html.replace(/(\s)(\u2014)(\s)/g, '$1<span style="white-space: nowrap;">\u2014</span>$3');
        html = html.replace(/\s(в|на|за|с|под|над|от|до|из|к|по|со|у|о|об|без|для|и|а|но|или|из-за|я|ты|он|мы|вы|они|ли|бы|же|не|ни)\s/gi, ' $1&nbsp;');
        html = html.replace(/(\s)([.,!?;:])/g, '&nbsp;$2');
        
        if (html !== el.innerHTML) {
            el.innerHTML = html;
        }
    });
}

function isSimpleTextElement(el) {
    const children = el.children;
    for (let i = 0; i < children.length; i++) {
        const tagName = children[i].tagName.toLowerCase();
        if (!['a', 'br', 'span', 'strong', 'em', 'b', 'i', 'mark', 'small', 'sub', 'sup'].includes(tagName)) {
            return false;
        }
    }
    return true;
}

function initNbspObserver() {
    const observer = new MutationObserver((mutations) => {
        let shouldApply = false;
        
        mutations.forEach((mutation) => {
            if (mutation.type === 'childList') {
                mutation.addedNodes.forEach((node) => {
                    if (node.nodeType === 1) { 
                        shouldApply = true;
                        applyNbspToNode(node);
                    }
                });
            }
        });
        
        if (shouldApply) {
            applyNbsp();
        }
    });
    
    observer.observe(document.body, {
        childList: true,
        subtree: true
    });
    
    applyNbsp();
}

function applyNbspToNode(node) {
    applyNbsp();
}

if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initNbspObserver);
} else {
    initNbspObserver();
}


