//Дата статьи

function formatDate(dateString) {
    const months = [
        'января', 'февраля', 'марта', 'апреля', 'мая', 'июня',
        'июля', 'августа', 'сентября', 'октября', 'ноября', 'декабря'
    ];
    
    const date = new Date(dateString);
    const day = date.getDate();
    const month = months[date.getMonth()];
    const year = date.getFullYear();
    
    return `${day} ${month} ${year}`;
}

function insertArticleDate() {
    const currentPath = window.location.pathname;
    const articleSlug = currentPath.split('/').pop().replace('.html', '');
    
    const currentArticle = window.allArticles.find(article => 
        article.article === articleSlug
    );
    
    if (!currentArticle || !currentArticle.date) return;
    
    const dateElement = document.querySelector('.article-date');
    if (dateElement) {
        dateElement.textContent = formatDate(currentArticle.date);
    }
}

document.addEventListener('DOMContentLoaded', insertArticleDate);
